    &reftitle.examples;



     Exemplos de Expressões Regulares


ereg ("abc", $string);
/* Retorna true se "abc"
   existir em qualquer lugar de $string. */

ereg ("^abc", $string);
/* Retorna true se "abc";
   existir no início de $string. */

ereg ("abc$", $string);
/* Retorna true se "abc"
   existir no final de $string. */

eregi ("(ozilla.[23]|MSIE.3)", $HTTP_USER_AGENT);
/* Retorna true se o navegador do cliente
   for Netscape 2, 3 ou MSIE 3. */

ereg ("([[:alnum:]]+) ([[:alnum:]]+) ([[:alnum:]]+)", $string,$regs);
/* Adiciona três palavras separadas por espaço
   em $regs[1], $regs[2] e $regs[3]. */

$string = ereg_replace ("^", "<br />", $string);
/* Acrescenta a tag <br /> no início de $string. */

$string = ereg_replace ("$", "<br />", $string);
/* Acrescenta a tag <br />; no final de $string. */

$string = ereg_replace ("\n", "", $string);
/* Remove todos caracteres de nova linha (newline)
   em $string. */








    &reftitle.seealso;


     Para expressões regulares compatatíveis com a sintaxe do PERL olhe as
     Funções PCRE.




